export function DialogDemo() {
  //Controls the visual state of dialog
  const [showConfirmDialog, setShowConfirmDialog] = useState(false);
  const { toast } = useToast();

  const handleOpenDialog = () => {
    setShowConfirmDialog(true);
  };

  const handleCancel = () => {
    setShowConfirmDialog(false);
    toast({
      title: "Phew, that was close!",
    });
  };
  const handleConfirm = () => {
    setShowConfirmDialog(false);
    toast({
      title: "Account deleted",
      variant: "destructive",
    });
  };

  return (
    <>
      <Button onClick={handleOpenDialog}>Delete Account</Button>
      <Confirmation
        // Additional props to make this a reusuable component
        title="Are you sure?"
        description="This action is permanent. You cannot undo this action."
        confirmLabel="Delete Account"
        isOpen={showConfirmDialog}
        // Actions to be run upon cancellation.
        onCancel={handleCancel}
        onConfirm={handleConfirm}
      />
      <Toaster />
    </>
  );
}

type ConfirmationProps = {
  title: React.ReactNode,
  description: React.ReactNode,
  confirmLabel: string,
  isOpen: boolean,
  onConfirm: () => void,
  onCancel: () => void,
};
const Confirmation = (props: ConfirmationProps) => {
  return (
    //Alert Dialog shows the component depending on the state of the open prop
    <AlertDialog open={props.isOpen}>
      <AlertDialogContent>
        <AlertDialogHeader>
          <AlertDialogTitle>{props.title}</AlertDialogTitle>
          <AlertDialogDescription>{props.description}</AlertDialogDescription>
        </AlertDialogHeader>
        <AlertDialogFooter>
          <AlertDialogCancel onClick={props.onCancel}>Cancel</AlertDialogCancel>
          <AlertDialogAction onClick={props.onConfirm}>
            {props.confirmLabel}
          </AlertDialogAction>
        </AlertDialogFooter>
      </AlertDialogContent>
    </AlertDialog>
  );
};

