//If you're using typescript, casting the promise as a boolean allows the
//typechecker to know that the result of the promise will be a boolean

const isEven = (n: number) =>
  new Promise() <
  boolean >
  ((resolve, reject) => {
    /* If the promise cannot be completed, reject with a response 
			 that can be caught with error handling
		*/
    if (typeof n !== "number") {
      reject("invalid input type");
    }

    // Execute the code within the promise
    const result = n % 2 === 0;
    /* Resolve with the result which will be accessible after the promise is 
		awaited */
    resolve(result);
  });

const isFiveEven = await isEven(5);
//isFiveEven results in false

